#-----------------------------------------------------------------------
Filepath:
#-----------------------------------------------------------------------
HERE
cat Filepath >>$VERSION_FILE

cat >>$VERSION_FILE <<HERE
#-----------------------------------------------------------------------
END SUMMARY
#-----------------------------------------------------------------------
HERE


if [ `which vim 2>/dev/null` ]; then
EX_CMD="vim -e -s -X"
else
EX_CMD="ex -s"
fi

$EX_CMD $VERSION_FILE <<HERE
%s/\\\\/\\\\\\\\/
%s/\(.*\)/write(*,*) "\1"/
1i
subroutine lis_version()
implicit none
.
\$a
end subroutine lis_version
.
wq
HERE
